import React, { useState, useEffect } from 'react';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import axios from 'axios';

interface DashboardStats {
  totalDonors: number;
  activeDonors: number;
  recentRegistrations: number;
  livesSaved: number;
}

interface RecentDonor {
  id: string;
  name: string;
  blood_type: string;
  registration_date: string;
}

const AdminDashboard: React.FC = () => {
  const [stats, setStats] = useState<DashboardStats>({
    totalDonors: 0,
    activeDonors: 0,
    recentRegistrations: 0,
    livesSaved: 0
  });

  const [recentDonors, setRecentDonors] = useState<RecentDonor[]>([]);
  const [loading, setLoading] = useState(true);
  const [username, setUsername] = useState('');
  const [role, setRole] = useState('');
  const [sidebarCollapsed, setSidebarCollapsed] = useState(false);

  const navigate = useNavigate();
  const location = useLocation();

  useEffect(() => {
    // Check authentication
    const isAuthenticated = localStorage.getItem('isAuthenticated');
    if (!isAuthenticated) {
      navigate('/login');
      return;
    }

    const storedUsername = localStorage.getItem('username') || 'Admin';
    const storedRole = localStorage.getItem('role') || 'admin';
    setUsername(storedUsername);
    setRole(storedRole);

    fetchDashboardData();
  }, [navigate]);

  const fetchDashboardData = async () => {
    try {
      const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:8000/api';

      // Fetch stats and donors
      const [statsResponse, donorsResponse] = await Promise.all([
        axios.get(`${API_BASE_URL}/bloodbank/stats`),
        axios.get(`${API_BASE_URL}/bloodbank/donors`)
      ]);

      if (statsResponse.data.stats) {
        setStats({
          totalDonors: donorsResponse.data.total || 0,
          activeDonors: statsResponse.data.stats.activeDonors || 0,
          recentRegistrations: Math.floor((donorsResponse.data.total || 0) * 0.2),
          livesSaved: statsResponse.data.stats.livesSaved || 0
        });
      }

      if (donorsResponse.data.donors) {
        const recent = donorsResponse.data.donors
          .sort((a: RecentDonor, b: RecentDonor) => 
            new Date(b.registration_date).getTime() - new Date(a.registration_date).getTime()
          )
          .slice(0, 5);
        setRecentDonors(recent);
      }
    } catch (error) {
      console.error('Error fetching dashboard data:', error);
      // Use mock data if API is not available
      setStats({
        totalDonors: 25,
        activeDonors: 22,
        recentRegistrations: 5,
        livesSaved: 50
      });
    } finally {
      setLoading(false);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('isAuthenticated');
    localStorage.removeItem('username');
    localStorage.removeItem('role');
    navigate('/login');
  };

  const getRoleColor = (role: string) => {
    switch (role.toLowerCase()) {
      case 'admin': return 'bg-danger';
      case 'manager': return 'bg-warning text-dark';
      case 'staff': return 'bg-primary';
      default: return 'bg-secondary';
    }
  };

  const getBloodTypeColor = (bloodType: string) => {
    const colors: { [key: string]: string } = {
      'A+': 'bg-danger text-white',
      'A-': 'bg-danger-subtle text-danger',
      'B+': 'bg-primary text-white',
      'B-': 'bg-primary-subtle text-primary',
      'AB+': 'bg-success text-white',
      'AB-': 'bg-success-subtle text-success',
      'O+': 'bg-warning text-dark',
      'O-': 'bg-warning-subtle text-warning'
    };
    return colors[bloodType] || 'bg-secondary text-white';
  };

  const isActiveRoute = (path: string) => {
    return location.pathname === path;
  };

  if (loading) {
    return (
      <div className="d-flex align-items-center justify-content-center min-vh-100" 
           style={{ background: 'linear-gradient(135deg, #dc3545 0%, #8b0000 100%)' }}>
        <div className="text-center text-white">
          <div className="spinner-border mb-3" style={{ width: '3rem', height: '3rem' }}>
            <span className="visually-hidden">Loading...</span>
          </div>
          <p>Loading dashboard...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="d-flex min-vh-100">
      {/* Sidebar */}
      <div className={`bg-dark text-white position-fixed h-100 ${sidebarCollapsed ? 'collapsed' : ''}`} 
           style={{ 
             width: sidebarCollapsed ? '80px' : '280px',
             transition: 'width 0.3s ease',
             zIndex: 1000,
             overflowY: 'auto'
           }}>

        {/* Logo */}
        <div className="p-3 border-bottom border-secondary">
          <div className="d-flex align-items-center">
            <img src="/yuva.jpg" alt="Yuva" width="40" height="40" className="rounded-circle" />
            {!sidebarCollapsed && (
              <div className="ms-3">
                <h6 className="text-white mb-0">Yuva Blood Bank</h6>
                <small className="text-muted">Management System</small>
              </div>
            )}
          </div>
        </div>

        {/* User Info */}
        <div className="p-3 border-bottom border-secondary">
          <div className="d-flex align-items-center">
            <div className="bg-primary rounded-circle d-flex align-items-center justify-content-center" 
                 style={{width: '40px', height: '40px'}}>
              <span className="text-white fw-bold">{username.charAt(0).toUpperCase()}</span>
            </div>
            {!sidebarCollapsed && (
              <div className="ms-3">
                <h6 className="text-white mb-1">{username}</h6>
                <span className={`badge ${getRoleColor(role)} small`}>{role.toUpperCase()}</span>
              </div>
            )}
          </div>
        </div>

        {/* Navigation Menu */}
        <nav className="flex-grow-1 py-3">
          <ul className="nav flex-column">

            {/* Dashboard */}
            <li className="nav-item">
              <Link to="/admin" 
                    className={`nav-link text-white d-flex align-items-center px-3 py-3 ${isActiveRoute('/admin') ? 'bg-primary' : ''}`}
                    style={{transition: 'all 0.3s ease'}}>
                <i className="fas fa-tachometer-alt me-3" style={{width: '20px'}}></i>
                {!sidebarCollapsed && <span>Dashboard</span>}
              </Link>
            </li>

            {/* Register Donor */}
            <li className="nav-item">
              <Link to="/register" 
                    className={`nav-link text-white d-flex align-items-center px-3 py-3 ${isActiveRoute('/register') ? 'bg-primary' : ''}`}
                    style={{transition: 'all 0.3s ease'}}>
                <i className="fas fa-user-plus me-3" style={{width: '20px'}}></i>
                {!sidebarCollapsed && <span>Register Donor</span>}
              </Link>
            </li>

            {/* Donor Listing */}
            <li className="nav-item">
              <Link to="/donors" 
                    className={`nav-link text-white d-flex align-items-center px-3 py-3 ${isActiveRoute('/donors') ? 'bg-primary' : ''}`}
                    style={{transition: 'all 0.3s ease'}}>
                <i className="fas fa-list me-3" style={{width: '20px'}}></i>
                {!sidebarCollapsed && <span>Donor Listing</span>}
              </Link>
            </li>

            {/* Reports */}
            <li className="nav-item">
              <Link to="/reports" 
                    className={`nav-link text-white d-flex align-items-center px-3 py-3 ${isActiveRoute('/reports') ? 'bg-primary' : ''}`}
                    style={{transition: 'all 0.3s ease'}}>
                <i className="fas fa-chart-bar me-3" style={{width: '20px'}}></i>
                {!sidebarCollapsed && <span>Reports</span>}
              </Link>
            </li>

            {/* Search */}
            <li className="nav-item">
              <Link to="/search" 
                    className={`nav-link text-white d-flex align-items-center px-3 py-3 ${isActiveRoute('/search') ? 'bg-primary' : ''}`}
                    style={{transition: 'all 0.3s ease'}}>
                <i className="fas fa-search me-3" style={{width: '20px'}}></i>
                {!sidebarCollapsed && <span>Search Donors</span>}
              </Link>
            </li>

            {/* Divider */}
            <li className="my-3">
              <hr className="border-secondary" />
            </li>

            {/* Settings */}
            <li className="nav-item">
              <Link to="/settings" 
                    className={`nav-link text-white d-flex align-items-center px-3 py-3 ${isActiveRoute('/settings') ? 'bg-primary' : ''}`}
                    style={{transition: 'all 0.3s ease'}}>
                <i className="fas fa-cog me-3" style={{width: '20px'}}></i>
                {!sidebarCollapsed && <span>Settings</span>}
              </Link>
            </li>

            {/* Logout */}
            <li className="nav-item">
              <button onClick={handleLogout}
                      className="nav-link text-white d-flex align-items-center px-3 py-3 w-100 border-0 bg-transparent text-start"
                      style={{transition: 'all 0.3s ease'}}>
                <i className="fas fa-sign-out-alt me-3" style={{width: '20px'}}></i>
                {!sidebarCollapsed && <span>Logout</span>}
              </button>
            </li>
          </ul>
        </nav>
      </div>

      {/* Main Content */}
      <div className="flex-grow-1" style={{ 
        marginLeft: sidebarCollapsed ? '80px' : '280px', 
        transition: 'margin-left 0.3s ease',
        background: '#f8f9fa'
      }}>

        {/* Top Header */}
        <header className="bg-white shadow-sm border-bottom sticky-top">
          <div className="d-flex align-items-center justify-content-between p-3">
            <div className="d-flex align-items-center">
              <button 
                className="btn btn-outline-secondary me-3"
                onClick={() => setSidebarCollapsed(!sidebarCollapsed)}
              >
                <i className="fas fa-bars"></i>
              </button>
              <h4 className="mb-0 text-primary">Dashboard</h4>
            </div>
            <div className="d-flex align-items-center">
              <span className="text-muted">
                <i className="fas fa-calendar me-2"></i>
                {new Date().toLocaleDateString('en-US', { 
                  weekday: 'long', 
                  year: 'numeric', 
                  month: 'long', 
                  day: 'numeric' 
                })}
              </span>
            </div>
          </div>
        </header>

        {/* Content Area */}
        <main className="p-4">

          {/* Welcome Card */}
          <div className="row mb-4">
            <div className="col-12">
              <div className="card border-0 shadow-sm">
                <div className="card-body p-4">
                  <div className="row align-items-center">
                    <div className="col-md-8">
                      <h2 className="text-dark mb-2">Welcome back, {username}! 👋</h2>
                      <p className="text-muted mb-0">
                        Here's what's happening with your blood bank today.
                      </p>
                    </div>
                    <div className="col-md-4 text-md-end">
                      <span className={`badge ${getRoleColor(role)} px-3 py-2 fs-6`}>
                        <i className="fas fa-shield-alt me-2"></i>
                        {role.toUpperCase()}
                      </span>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>

          {/* Statistics Cards */}
          <div className="row mb-4">
            <div className="col-lg-3 col-md-6 mb-4">
              <div className="card border-0 shadow-sm h-100">
                <div className="card-body">
                  <div className="d-flex align-items-center">
                    <div className="bg-primary bg-gradient rounded-3 p-3 me-3">
                      <i className="fas fa-users fa-2x text-white"></i>
                    </div>
                    <div>
                      <h3 className="text-dark mb-1">{stats.totalDonors}</h3>
                      <p className="text-muted mb-0 small">Total Donors</p>
                    </div>
                  </div>
                </div>
              </div>
            </div>

            <div className="col-lg-3 col-md-6 mb-4">
              <div className="card border-0 shadow-sm h-100">
                <div className="card-body">
                  <div className="d-flex align-items-center">
                    <div className="bg-success bg-gradient rounded-3 p-3 me-3">
                      <i className="fas fa-heartbeat fa-2x text-white"></i>
                    </div>
                    <div>
                      <h3 className="text-dark mb-1">{stats.activeDonors}</h3>
                      <p className="text-muted mb-0 small">Active Donors</p>
                    </div>
                  </div>
                </div>
              </div>
            </div>

            <div className="col-lg-3 col-md-6 mb-4">
              <div className="card border-0 shadow-sm h-100">
                <div className="card-body">
                  <div className="d-flex align-items-center">
                    <div className="bg-warning bg-gradient rounded-3 p-3 me-3">
                      <i className="fas fa-user-plus fa-2x text-white"></i>
                    </div>
                    <div>
                      <h3 className="text-dark mb-1">{stats.recentRegistrations}</h3>
                      <p className="text-muted mb-0 small">New This Month</p>
                    </div>
                  </div>
                </div>
              </div>
            </div>

            <div className="col-lg-3 col-md-6 mb-4">
              <div className="card border-0 shadow-sm h-100">
                <div className="card-body">
                  <div className="d-flex align-items-center">
                    <div className="bg-danger bg-gradient rounded-3 p-3 me-3">
                      <i className="fas fa-hands-helping fa-2x text-white"></i>
                    </div>
                    <div>
                      <h3 className="text-dark mb-1">{stats.livesSaved}</h3>
                      <p className="text-muted mb-0 small">Lives Saved</p>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>

          {/* Quick Actions & Recent Activity */}
          <div className="row">
            {/* Quick Actions */}
            <div className="col-lg-6 mb-4">
              <div className="card border-0 shadow-sm h-100">
                <div className="card-header bg-white border-0 pb-0">
                  <h5 className="card-title mb-0">
                    <i className="fas fa-bolt me-2 text-primary"></i>
                    Quick Actions
                  </h5>
                </div>
                <div className="card-body">
                  <div className="row g-3">
                    <div className="col-6">
                      <Link to="/register" className="btn btn-primary w-100 py-3 text-decoration-none">
                        <i className="fas fa-user-plus fa-2x mb-2 d-block"></i>
                        <span className="small">Register New Donor</span>
                      </Link>
                    </div>
                    <div className="col-6">
                      <Link to="/donors" className="btn btn-success w-100 py-3 text-decoration-none">
                        <i className="fas fa-list fa-2x mb-2 d-block"></i>
                        <span className="small">View All Donors</span>
                      </Link>
                    </div>
                    <div className="col-6">
                      <Link to="/reports" className="btn btn-warning w-100 py-3 text-decoration-none">
                        <i className="fas fa-chart-bar fa-2x mb-2 d-block"></i>
                        <span className="small">Generate Reports</span>
                      </Link>
                    </div>
                    <div className="col-6">
                      <Link to="/search" className="btn btn-info w-100 py-3 text-decoration-none">
                        <i className="fas fa-search fa-2x mb-2 d-block"></i>
                        <span className="small">Search Donors</span>
                      </Link>
                    </div>
                  </div>
                </div>
              </div>
            </div>

            {/* Recent Activity */}
            <div className="col-lg-6 mb-4">
              <div className="card border-0 shadow-sm h-100">
                <div className="card-header bg-white border-0 pb-0">
                  <h5 className="card-title mb-0">
                    <i className="fas fa-clock me-2 text-success"></i>
                    Recent Registrations
                  </h5>
                </div>
                <div className="card-body">
                  {recentDonors.length === 0 ? (
                    <div className="text-center py-4">
                      <i className="fas fa-users fa-3x text-muted mb-3"></i>
                      <p className="text-muted">No recent registrations</p>
                      <Link to="/register" className="btn btn-primary btn-sm">
                        Register First Donor
                      </Link>
                    </div>
                  ) : (
                    <div className="list-group list-group-flush">
                      {recentDonors.slice(0, 5).map((donor, index) => (
                        <div key={donor.id || index} className="list-group-item border-0 px-0 py-3">
                          <div className="d-flex align-items-center">
                            <div className="bg-primary text-white rounded-circle d-flex align-items-center justify-content-center me-3"
                                 style={{width: '45px', height: '45px', fontSize: '16px'}}>
                              {donor.name.charAt(0).toUpperCase()}
                            </div>
                            <div className="flex-grow-1">
                              <h6 className="mb-1">{donor.name}</h6>
                              <small className="text-muted">
                                <i className="fas fa-calendar me-1"></i>
                                {new Date(donor.registration_date).toLocaleDateString()}
                              </small>
                            </div>
                            <span className={`badge ${getBloodTypeColor(donor.blood_type)} px-2 py-1`}>
                              {donor.blood_type}
                            </span>
                          </div>
                        </div>
                      ))}
                    </div>
                  )}
                </div>
              </div>
            </div>
          </div>
        </main>
      </div>
    </div>
  );
};

export default AdminDashboard;
